#include <windows.h>
#include <GL/gl.h>

// cp-L
#ifndef tex_t
#define tex_t unsigned int
#endif 

#ifndef uchar
#define uchar unsigned char
#endif

tex_t tex_conChars = 0;
GLubyte _cpl_indices[8] = { 0,1,2,3,4,5,6,7 };

void cpl_initconChars();

int cpl_offsetXConChar(char c) {
	return c % 16;
}

int cpl_offsetYConChar(char c) {
	int i = 0;
	while (c >= 16) {
		c -= 16;
		i++;
	}
	return i;
}

void txBegin(tex_t tex) {
    glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL_NEAREST);
    glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_NEAREST);
	glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_S, GL_REPEAT);
	glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_T, GL_REPEAT);
	glEnable(GL_TEXTURE_2D);
	glBindTexture(GL_TEXTURE_2D, tex);
}

void txEnd() {
	glDisable(GL_TEXTURE_2D);
	glBindTexture(GL_TEXTURE_2D, -1);
}

void cpl_drawConNoColorChar(char c, int x, int y) {
	if (!tex_conChars) {
		cpl_initconChars();
	}
	if (c == ' ') return;
	float ofx = cpl_offsetXConChar(c) * 0.0625;
	float ofy = cpl_offsetYConChar(c) * 0.125;
	glEnable(GL_BLEND);
	glBlendFunc(GL_SRC_ALPHA, GL_ONE_MINUS_SRC_ALPHA);
	txBegin(tex_conChars);
	glEnableClientState(GL_TEXTURE_COORD_ARRAY);
	glEnableClientState(GL_VERTEX_ARRAY);
	GLfloat v[8] = {
		x,		y + 2,
		x,		y + 2 + 16,
		x + 16,	y + 2 + 16,
		x + 16,	y + 2
	};
	GLfloat tc[8] = {
		ofx			, ofy,
		ofx			, ofy + 0.125,
		ofx + 0.0625, ofy + 0.125,
		ofx + 0.0625, ofy
	};
	glTexCoordPointer(2, GL_FLOAT, 0, tc);
	glVertexPointer(2, GL_FLOAT, 0, v);
	glDrawElements(GL_QUADS, 8, GL_UNSIGNED_BYTE, _cpl_indices);
	glDisableClientState(GL_TEXTURE_COORD_ARRAY);
	glDisableClientState(GL_VERTEX_ARRAY);
	txEnd();
	glDisable(GL_BLEND);
}

void cpl_drawConNoColorStringN(char* c, int x, int y, int N) {
	int xx = x;
	for (int i = 0; i < N; i++) {
		if (c[i] == '\0') return;
		cpl_drawConChar(c[i], xx, y);
		xx += 16;
	}
}

void cpl_drawConChar(char c, int x, int y) {
	glColor4f(1, 1, 1, 1);
	cpl_drawConNoColorChar(c, x, y);
}

void cpl_drawConStringN(char* c, int x, int y, int N) {
	glColor4f(1, 1, 1, 1);
	cpl_drawConNoColorStringN(c, x, y, N);
}

