void cpl_set2byte(uchar* c, int index, uchar value) {
	if (index < 0 || index > 3 || value > 3) return;
	uchar mask = 0x03 << (index * 2);
	*c &= ~mask;
	*c |= (value << (index * 2));
}

uchar cpl_get2byte(uchar c, int index) {
	if (index < 0 || index > 3) return 0;
	return (c >> (index * 2)) & 0x03;
}

uchar* cpl_2bitChars_to_image(uchar* bufIn, unsigned int lenght) {
	uchar* ch = malloc(sizeof(uchar) * lenght * 16);

	int j = 0;
	for (unsigned int i = 0; i < lenght; i++) {
		uchar c = bufIn[i];
		for (int k = 0; k < 4; k++) {
			char n = cpl_get2byte(c, k);
			ch[j    ] = 0;
			ch[j + 1] = 0;
			ch[j + 2] = 0;
			ch[j + 3] = 0;
			if (n == 1) {
				ch[j + 3] = 255;
			}
			if (n == 2) {
				ch[j    ] = 127;
				ch[j + 1] = 127;
				ch[j + 2] = 127;
				ch[j + 3] = 255;
			}
			j += 4;
			//
		}
	}

	return ch;
}

void cpl_initconChars() {
     
    glGenTextures(1, &tex_conChars);
    glBindTexture(GL_TEXTURE_2D, tex_conChars);

    glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL_NEAREST);
    glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_NEAREST);
    glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_S, GL_REPEAT);
    glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_T, GL_REPEAT);

    glTexImage2D(GL_TEXTURE_2D, 0, GL_RGBA, 256, 128, 0, GL_RGBA, GL_UNSIGNED_BYTE, cpl_2bitChars_to_image(chars_data, 8192));
	
    glBindTexture(GL_TEXTURE_2D, -1);
}